INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252601, 'Glecaprevir', 'Digoxin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the serum concentrations of digoxin.  The proposed mechanism is inhibition of the P-glycoprotein (P-gp)-mediated intestinal efflux or renal tubular secretion of digoxin by glecaprevir and pibrentasvir, both of which are inhibitors of the transporter.', NULL, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir.', NULL, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir.  The manufacturer recommends measuring serum digoxin levels before initiating glecaprevir-pibrentasvir and reducing the levels by decreasing the digoxin dose approximately 50% or by modifying the dosing frequency, followed by continued monitoring.  Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Digitoxin', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299992/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252602, 'Glecaprevir', 'Dulaglutide', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Guar gum', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Dolutegravir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299993/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[6] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb  (2015):[7] Cerner Multum, Inc "Malaysia product information." O 0 (2015):[8] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):[9] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences  (2016):[10] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):[11] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252603, 'Glecaprevir', 'Edoxaban', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', NULL, 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', NULL, 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.  This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.  Other P-gp inhibitors were not permitted in the study, and patients on antiretroviral therapy (ritonavir, nelfinavir, indinavir, saquinavir) as well as cyclosporine were excluded from the study.  Following discontinuation of the P-gp inhibitor, edoxaban dosage should be returned to the regular dosage of 60 mg once daily.  No dosage adjustment is recommended for edoxaban when used in the treatment of nonvalvular atrial fibrillation.', 'Absorption', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Etravirine, Dolutegravir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299995/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Mendell J, Zahir H, Matsushima N, et al. "Drug-Drug Interaction Studies of Cardiovascular Drugs Involving P-Glycoprotein, an Efflux Transporter, on the Pharmacokinetics of Edoxaban, an Oral Factor Xa Inhibitor." Am J Cardiovasc Drugs  (2013):[3] "Product Information. Savaysa (edoxaban)." Daiichi Sankyo, Inc.  (2015):[4] Parasrampuria DA, Mendell J, Shi M, Matsushima N, Zahir H, Truitt K "Edoxaban drug閳ユ徆rug interactions with ketoconazole, erythromycin, and cyclosporine." Br J Clin Pharmacol 0 (2016):  epub', 1767369485);
