INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252677, 'Glecaprevir', 'Pioglitazone', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Guar gum', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Telaprevir, Rilpivirine, Dolutegravir, Cobicistat, Brincidofovir, Famciclovir, Doravirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300114/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[6] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb  (2015):[7] Cerner Multum, Inc "Malaysia product information." O 0 (2015):[8] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):[9] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences  (2016):[10] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):[11] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252678, 'Glecaprevir', 'Pitavastatin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the plasma concentrations of all statins including fluvastatin and pitavastatin.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by glecaprevir and pibrentasvir.  Although the interaction has not been studied specifically with fluvastatin or pitavastatin, it has been reported with other statins that are also substrates of OATP1B1.  High levels of HMG-CoA reductase inhibitory activity in plasma may be associated with an increased risk of musculoskeletal toxicity.', NULL, 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir.', NULL, 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir.  The lowest approved dosage of the statin should be used.  If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Metabolism, Distribution', 'Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Cerivastatin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300116/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
