INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252684, 'Glecaprevir', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally.  Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours.  Patients should be monitored more frequently for adverse effects.  Alternatively, when relugolix is used as monotherapy for the treatment of prostate cancer, the prescribing information states that treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.  Following interruption of relugolix for more than 7 days, the manufacturer recommends restarting therapy with a loading dose of 360 mg on the first day, then continuing with a dose of 120 mg once daily.', 'Absorption', 'Norethisterone, Nafarelin, Octreotide, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Tamoxifen, Anastrozole, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300125/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc.  (2021):', 1767369485);
