INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252687, 'Glecaprevir', 'Rifampicin', 'Major', 'The plasma concentrations of glecaprevir may increase significantly during acute coadministration with rifampin, but decreases after chronic coadministration.  Pibrentasvir plasma concentrations are not affected during acute coadministration with rifampin, but also decreases significantly after chronic coadministration.  The proposed mechanism is rifampin inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir initially, followed by induction of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of glecaprevir and pibrentasvir after chronic dosing.', NULL, 'Concomitant use of glecaprevir and pibrentasvir with rifampin is considered contraindicated.', NULL, 'Concomitant use of glecaprevir and pibrentasvir with rifampin is considered contraindicated.', 'Metabolism, Absorption, Distribution', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, More', 'Paritaprevir, Zanamivir, Velpatasvir, Elbasvir, Famciclovir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, Telbivudine, Entecavir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300132/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
