INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252709, 'Glecaprevir', 'Tipranavir', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) or CYP450 3A4 may decrease the plasma concentrations of glecaprevir and pibrentasvir.  Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', NULL, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', 'Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Famciclovir, Tipranavir, Doravirine, Bictegravir, Ribavirin, Maribavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300165/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
