INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252876, 'Homatropine', 'Amantadine', 'Moderate', 'The anticholinergic-like adverse effects of amantadine may be potentiated by agents with anticholinergic properties such as antihistamines, antispasmodics, class IA antiarrhythmics, neuroleptics, phenothiazines, skeletal muscle relaxants, and tricyclic antidepressants.', NULL, 'Caution is advised when amantadine is used in combination with anticholinergic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised when amantadine is used in combination with anticholinergic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.  Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.  Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.  A reduction in anticholinergic and/or amantadine dosage may be necessary if excessive adverse effects develop.', 'Synergism', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, More', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300366/', '[1] Postma JU, van Tilburg W "Visual hallucinations and delirium during treatment with amantadine (Symmetrel)." J Am Geriatr Soc 23 (1975):  212-5[2] "Product Information. Symmetrel (amantadine)." DuPont Pharmaceuticals  (2002):', 1767369485);
