INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252931, 'Homatropine', 'Edrophonium', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine).  Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.  In addition, such use may mask the less serious, gastrointestinal signs of cholinergic overdose and lead to inadvertent induction of cholinergic crisis, which can produce respiratory paralysis and death.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.  If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.  Caution is advised not only because anticholinergic agents may mask the signs of a cholinergic overdose, but also because increasing muscle weakness associated with disease aggravation may be difficult to distinguish from that due to cholinergic crisis.', 'Antagonism', 'Sorbitol, Caffeine, Indocyanine green, Tolbutamide, Inulin, Hexaminolevulinate, Secretin human, Galactose, Corticorelin, Macimorelin, Methacholine, More', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300424/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc  (2001):', 1767369485);
