INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126567, 'Neostigmine', 'Labetalol', 'Moderate', 'Severe bradycardia and hypotension may develop during concomitant administration of some beta-blockers and anticholinesterase agents. The mechanism is unknown but may involve additive effects.', NULL, 'The patient''s hemodynamic status should be monitored during coadministration.', NULL, 'The patient''s hemodynamic status should be monitored during coadministration. Patients should be advised to notify their physician if they experience syncope, orthostasis, or a slow pulse.', 'Others', 'Ambenonium, Bethanechol, Pyridostigmine, Latanoprost, Methazolamide, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, Travoprost, Carteolol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126721/', '[1] Eldor J, Hoffman B, Davidson JT "Prolonged bradycardia and hypotension after neostigmine administration in a patient receiving atenolol." Anaesthesia 42 (1987): 1294-7', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126568, 'Nicardipine', 'Labetalol', 'Moderate', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers are used concomitantly with beta blockers, particularly in patients with ventricular or conduction abnormalities. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. The proposed mechanisms include additive slowing in AV conduction, reduced cardiac contractility secondary to beta-blockade, and decreased peripheral vascular resistance secondary to calcium channel blockade. In addition, some calcium channel blockers may inhibit the CYP450 metabolism of hepatically metabolized beta blockers, resulting in increased serum concentrations.', NULL, 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary.', NULL, 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'Metabolism, Synergism', 'Mibefradil', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126722