INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126701, 'Fingolimod', 'Lacosamide', 'Moderate', 'The risk of bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or AV conduction such as alectinib, atazanavir, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction. Close monitoring is recommended during initiation of fingolimod treatment in these patients. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', 'Brivaracetam, Topiramate, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126855/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
