INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127055, 'Raltegravir', 'Lansoprazole', 'Minor', 'Since raltegravir solubility is increased at higher pH, coadministration with agents that elevate gastric pH may theoretically increase the plasma concentrations of raltegravir. However, concurrent use of proton pump inhibitors or H2-receptor antagonists in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with proton pump inhibitors or H2-receptor antagonists.', NULL, '-', NULL, '-', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Clarithromycin, Vonoprazan, Amoxicillin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Etravirine, Dolutegravir, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127209/', '[1] "Product Information. Isentress (raltegravir)." Merck & Company Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127056, 'Rifampicin', 'Lansoprazole', 'Moderate', 'Coadministration with potent inducers of CYP450 2C19 and/or 3A4, such as rifampin and St. John''s wort, may significantly decrease the plasma concentrations of drugs which are primarily metabolized by these isoenzymes.', NULL, 'Caution is advised when potent inducers of CYP450 2C19 and/or 3A4 are used concomitantly with medications that are substrates of these CYP450 isoenzymes.', NULL, 'Caution is advised when potent inducers of CYP450 2C19 and/or 3A4 are used concomitantly with medications that are substrates of these CYP450 isoenzymes. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever St. John''s wort or rifampin is added to or withdrawn from therapy.', 'Metabolism', 'Tetracycline, Levofloxacin, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Amoxicillin', 'Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Cycloserine, Rifabutin, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127210/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Perga