INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127145, 'Echinacea', 'Lapatinib', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'Entrectinib, Imatinib, Pralsetinib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127299/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127146, 'Entrectinib', 'Lapatinib', 'Moderate', 'Entrectinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of entrectinib and periodically during treatment as appropriate based on individual risk factors.', 'Synergism', 'Futibatinib, Entrectinib, Regorafenib, Copanlisib, Trametinib, Erlotinib, Ponatinib, Neratinib, Erdafitinib, Nintedanib, Capmatinib, More', 'Regorafenib, Bosutinib, Lapatinib, Trametinib, Dacomitinib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127300/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127147, 'Enzalutamide', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Alectinib, Dacomitinib', 'Anastrozole, Exemestane, Fulvestrant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127301/', '[1] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127148, 'Epirubicin', 'Lapatinib', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin', 'Dabrafenib, Acalabrutinib, Dacomitinib, Avapritinib, Bosutinib, Selumetinib, Ibrutinib, Tivozanib, Zanubrutinib, Lorlatinib, Ripretinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127302/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Car