INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127228, 'Letermovir', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to lapatinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lapatinib dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased adverse effects of lapatinib such as diarrhea, nausea, vomiting, dyspepsia, and fatigue.', 'Metabolism', 'Sunitinib, Futibatinib, Regorafenib, Tucatinib, Vemurafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Nintedanib, Ibrutinib, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127382/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127229, 'Letrozole', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', NULL, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Anastrozole, Exemestane, Fulvestrant', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Vandetanib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127383/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127230, 'Leuprolide', 'Lapatinib', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Ribociclib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127384/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767369485);
