INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127244, 'Lumateperone', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone, which is metabolized by the isoenzyme in vitro.', NULL, 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as dizziness, drowsiness, abnormal muscle movements, seizures, high blood sugar, diabetes, high cholesterol, weight gain, low white blood cell count, difficulty swallowing, and heat-related disorders such as heat intolerance or heat stroke.', 'Metabolism', 'Acalabrutinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Brexpiprazole, Loxapine, Molindone, Thiothixene', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127398/', '[1] "Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc., New York, NY.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc., New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127245, 'Lurasidone', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary.', NULL, 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Brexpiprazole, Loxapine, Molindone, Thiothixene', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127399/', '[1] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[7] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[8] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127246, 'Lurbinectedin', 'Lapatinib', 'Moderate', 'Coadministration with a CYP450 3A4 inhibitor may increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; or nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Niraparib, Selinexor, Aflibercept, Trastuzumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127400/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127247, 'Lusutrombopag', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors. Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Distribution', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, More', 'Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Epinephrine, Coagulation Factor IX Human', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127401/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127248, 'Macimorelin', 'Lapatinib', 'Major', 'Macimorelin can cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Sorbitol, Caffeine, Edrophonium, Indocyanine green, Tolbutamide, Inulin, Hexaminolevulinate, Secretin human, Galactose, Corticorelin, Methacholine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127402/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa 