INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127309, 'Pazopanib', 'Lapatinib', 'Major', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations and risk of toxicity of pazopanib. The proposed mechanism involves inhibition of P-gp- or BCRP-mediated transporters of pazopanib metabolism. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Concomitant use of pazopanib with potent inhibitors of P-gp or BCRP should generally be avoided.', NULL, 'Concomitant use of pazopanib with potent inhibitors of P-gp or BCRP should generally be avoided. Selection of an alternate concomitant medication with no or minimal potential to inhibit P-gp or BCRP should be considered. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', 'Regorafenib, Copanlisib, Abemaciclib, Erlotinib, Ponatinib, Neratinib, Erdafitinib, Trametinib, Nintedanib, Afatinib, Dacomitinib, More', 'Regorafenib, Bosutinib, Lapatinib, Trametinib, Dacomitinib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127463/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Tan AR, Gibbon DG, Stein MN, et al. "Effects of ketoconazole and esomeprazole on the pharmacokinetics of pazopanib in patients with solid tumors." Cancer Chemother Pharmacol 71 (2013): 1635-43[3] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[6] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127310, 'Pegaspargase', 'Lapatinib', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Niraparib, Selinexor, Aflibercept, Trastuzumab, More', 'Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Fedratinib, Avapritinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.sc