INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127360, 'Rivaroxaban', 'Lapatinib', 'Moderate', 'Coadministration with weak or moderate dual inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'Dacomitinib, Bosutinib, Vandetanib, Cobimetinib, Afatinib, Axitinib, Ripretinib, Lenvatinib, Erlotinib, Osimertinib, Infigratinib, More', 'Ticagrelor, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, Prasugrel, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127514/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.[2] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[3] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127361, 'Rivastigmine', 'Lapatinib', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Memantine, Ginkgo biloba, Aducanumab', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127515/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127362, 'Rolapitant', 'Lapatinib', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Absorption', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Selumetinib, Ribociclib, More', 'Scopolamine, Nabilone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127516/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc., Waltham, MA.', 1767369485);
