    ) VALUES (1400394, 'Cyclophosphamide', 'Rocuronium', 'Moderate', 'Some drugs may enhance the effects of neuromuscular blocking agents.', NULL, 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', NULL, 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
