        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400445, 'Dicoumarol', 'Cycloserine', 'Moderate', 'The hypoprothrombinemic effect of coumarin anticoagulants may be potentiated by cycloserine or its prodrug, terizidone. The mechanism of interaction has not been described.', NULL, 'Caution is advised when coumarin anticoagulants are coadministered with cycloserine or terizidone.', NULL, 'Caution is advised when coumarin anticoagulants are coadministered with cycloserine or terizidone. The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the antituberculosis agent. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
