    ) VALUES (1400460, 'Aluminum hydroxide', 'Cyclosporine', 'Moderate', 'INTERVAL: The blood concentrations of orally administered cyclosporine were reported to have decreased rapidly in a pediatric patient who had been administered cyclosporine combined with dried aluminum hydroxide gel. The proposed mechanism of this interaction is adsorption or complex formation by aluminum. Other aluminum salts may interact in a similar manner.', NULL, 'Oral cyclosporine should be administered 2 hours before or 6 hours after the aluminum salt in order to avoid this potential interaction.', NULL, 'Oral cyclosporine should be administered 2 hours before or 6 hours after the aluminum salt in order to avoid this potential interaction. The patient''s therapeutic response should be monitored.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
