        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400465, 'Anisindione', 'Cyclosporine', 'Moderate', 'A few case reports have suggested that the concomitant use of cyclosporine and warfarin may result in both increased and reduced effects of both agents, although this interaction is not well established. The mechanism is unknown. A similar reaction may occur with other oral coumarin anticoagulants.', NULL, 'It may be advisable to monitor prothrombin time (PT) and/or International Normalized Ratio(INR), as well as cyclosporine blood concentrations and the patient''s response is recommended if these agents are used together.', NULL, 'It may be advisable to monitor prothrombin time (PT) and/or International Normalized Ratio(INR), as well as cyclosporine blood concentrations and the patient''s response is recommended if these agents are used together.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
