INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400471, 'Cyclosporine', 'Berotralstat', 'Major', 'Coadministration of berotralstat with drugs that are both inhibitors of the efflux transporters P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) as well as substrates of the isoenzyme CYP450 3A4 may lead to an increase in the plasma concentrations and effects of both drugs. Berotralstat is a substrate of both P-gp and BCRP. Increased plasma concentrations of berotralstat may increase the risk of adverse effects, including the potential for QT prolongation. Berotralstat may cause concentration-dependent prolongation of the Fridericia-corrected QT interval (QTcF). In addition, berotralstat is considered a moderate CYP450 3A4 inhibitor and has been reported in drug interaction studies to increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of sensitive CYP450 3A4 substrate midazolam by approximately 1.5-fold and 2.25-fold, respectively.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine. Patients should be advised to contact their physician if they experience any undue adverse effects from their medications. Patients should seek prompt medical attention if they experience symptoms that could indicated the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400472, 'Cyclosporine', 'Bifidobacterium longum infantis', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
