        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400491, 'Dasatinib', 'Cyclosporine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if dasatinib is used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if dasatinib is used in combination with moderate CYP450 3A4 inhibitors. Close monitoring for toxicity (e.g., myelosuppression, bleeding complications, fluid retention) is recommended, and the dasatinib dosage adjusted as necessary. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
