INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127599, 'Pimozide', 'Larotrectinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated. Some authorities consider concomitant administration of lurasidone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. With respect to less potent CYP450 3A4 inhibitors, the manufacturers recommend that they also not be used with pimozide.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127753/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[3] Flockhart DA, Drici MD, Kerbusch T, et al "Studies on the mechanism of a fatal clarithromycin-pimozide interaction in a patient with tourette syndrome." J Clin Psychopharmacol 20 (2000): 317-24[4] Mangum EM, Graham KK "Lopinavir-Ritonavir: a new protease inhibitor." Pharmacotherapy 21 (2001): 1352-63[5] Desta Z, Kerbusch T, Flockhart DA "Effect of clarithromycin on the pharmacokinetics and pharmacodynamics of pimozide in healthy poor and extensive metabolizers of cytochrome P450 2D6 (CYP2D6)." Clin Pharmacol Ther 65 (1999): 10-20[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[8] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[9] Krahenbuhl S, Sauter B, Kupferschmidt H, Krause M, Wyss PA, Meier PJ "Case report: reversible QT prolongation with torsades de pointes in a patient with pimozide intoxication." Am J Med Sci 309 (1995): 315-6[10] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[11] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (127600, 'Pitolisant', 'Larotrectinib', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127754/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.', 1767365207);
