INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128016, 'Fingolimod', 'Lefamulin', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128170/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.[4] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128017, 'Flecainide', 'Lefamulin', 'Major', 'Lefamulin may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of lefamulin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of lefamulin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) and electrolyte levels assessed during treatment as appropriate based on individual risk factors. The recommended dosage and infusion rate of lefamulin should not be exceeded, as the magnitude of QT prolongation may increase with increasing concentrations of the drug or increasing the rate of infusion.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128171/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceu