INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128290, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Leflunomide', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128444/', '[1] "Product Information. Adenovirus Vaccine Type 4 and Type 7 (adenovirus vaccine, live)." Teva Pharmaceuticals USA, North Wales, PA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128291, 'Anisindione', 'Leflunomide', 'Moderate', 'Available data suggest that leflunomide may potentiate the anticoagulant effect of warfarin. The proposed mechanism is leflunomide inhibition of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of the more active S(-) enantiomer of warfarin.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with leflunomide.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with leflunomide. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation or discontinuation of leflunomide therapy in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128445/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P45