INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128474, 'Remdesivir', 'Leflunomide', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128628/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128475, 'Ribociclib', 'Leflunomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128629/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128476, 'Rifampicin', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. Pharmacokinetically, coadministration of leflunomide with rifampin may increase the plasma concentrations of M1, the active metabolite of leflunomide that is responsible for essentially all of its activity in vivo. The mechanism has not been described, but may involve rifampin induction of leflunomide metabolism via hepatic CYP450 isoenzymes. Due to the long half-life of M1, levels may continue to increase with multiple dosing of leflunomide.', NULL, 'Caution is advised if leflunomide is used in combination with rifampin.', NULL, 'Caution is advised if leflunomide is used in combination with rifampin. The potential for increased risk of leflunomide toxicities including peripheral neuropathy, immunosuppression, bone marrow suppression, and hepatotoxicity should be considered. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving leflunomide should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128630/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (128477, 'Rilonacept', 'Leflunomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Serious infections including sepsis, as well as opportunistic infections like Pneumocystis jiroveci pneumonia, pulmonary and extrapulmonary tuberculosis, and aspergillosis have been reported with the use of leflunomide, particularly in patients on concomitant hematotoxic therapy. Agents that may be significantly immuno- or myelosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids. Rare cases of pancytopenia, agranulocytosis, and thrombocytopenia have also occurred with leflunomide alone, but were most frequent in the presence of concomitant or recent use of methotrexate or other myelotoxic agents. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when hematotoxic agents are initiated after the discontinuation of leflunomide. Administering a washout procedure with cholestyramine or activated charcoal helps to accelerate elimination of the active metabolite from plasma and reduce the overlap of systemic exposure to these agents.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa. Platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If evidence of serious infection or bone marrow suppression occurs, treatment should be stopped, and cholestyramine or charcoal administered to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/128631/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.', 1767365207);
