        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400510, 'Donepezil', 'Cyclosporine', 'Minor', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of donepezil, which is primarily metabolized by these isoenzymes.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
