        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400519, 'Echinacea', 'Cyclosporine', 'Minor', 'Theoretically, agents that are thought to have immunostimulant properties such as echinacea, vitamin E, cat''s claw, and zinc may antagonize the pharmacologic effects of immunosuppressants.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
