        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400523, 'Elagolix', 'Cyclosporine', 'Major', 'Coadministration with potent inhibitors of organic anion transporting polypeptide (OATP) 1B1 may significantly increase the plasma concentrations of elagolix, which is a substrate of the hepatic uptake transporter.', NULL, 'Concomitant use of elagolix with potent OATP 1B1 inhibitors is considered contraindicated.', NULL, 'Concomitant use of elagolix with potent OATP 1B1 inhibitors is considered contraindicated.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
