        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400571, 'Glimepiride', 'Cyclosporine', 'Moderate', 'Glipizide may decrease the metabolism of cyclosporine. Cyclosporine levels may be increased by as much as 150%. It is not known whether other sulfonylureas will exhibit a similar interaction, although limited data do exist for glyburide. The proposed mechanism is inhibition of CYP450 hepatic metabolism.', NULL, 'If a sulfonylurea is added or discontinued in a patient stabilized on cyclosporine, serum cyclosporine levels should be closely monitored.', NULL, 'If a sulfonylurea is added or discontinued in a patient stabilized on cyclosporine, serum cyclosporine levels should be closely monitored. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, or headache.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400572, 'Glipizide', 'Cyclosporine', 'Moderate', 'Glipizide may decrease the metabolism of cyclosporine. Cyclosporine levels may be increased by as much as 150%. It is not known whether other sulfonylureas will exhibit a similar interaction, although limited data do exist for glyburide. The proposed mechanism is inhibition of CYP450 hepatic metabolism.', NULL, 'If a sulfonylurea is added or discontinued in a patient stabilized on cyclosporine, serum cyclosporine levels should be closely monitored.', NULL, 'If a sulfonylurea is added or discontinued in a patient stabilized on cyclosporine, serum cyclosporine levels should be closely monitored. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, or headache.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
