    ) VALUES (1400591, 'Indacaterol', 'Cyclosporine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the systemic exposure to indacaterol following oral inhalation, as it is a substrate of both the isoenzyme and efflux transporter.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
