    ) VALUES (1400592, 'Indapamide', 'Cyclosporine', 'Moderate', 'Coadministration of thiazide or thiazide-like diuretics with cyclosporine may potentially result in hypermagnesemia, hyperuricemia, and increase the risk of nephrotoxicity.', NULL, 'Monitoring of renal function, serum electrolytes, uric acid levels, and cyclosporine blood concentrations is advisable during concomitant use.', NULL, 'Monitoring of renal function, serum electrolytes, uric acid levels, and cyclosporine blood concentrations is advisable during concomitant use. Some authorities advise against concomitant use of these medicines.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
