        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400615, 'Ivabradine', 'Cyclosporine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme. Elevated plasma levels of ivabradine may increase the risk of excessive bradycardia and conduction disturbances.', NULL, 'Caution is advised when ivabradine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ivabradine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as bradycardia and atrial fibrillation, and the dosage of ivabradine adjusted as necessary in accordance with the product labeling', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
