        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400617, 'Cyclosporine', 'Ivosidenib', 'Moderate', 'Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Ivosidenib induces its own metabolism via CYP450 3A4.', NULL, 'Concomitant use of ivosidenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of ivosidenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. If coadministration is required, patients should be monitored carefully for potential loss of therapeutic effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
