        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400660, 'Cyclosporine', 'Mephenytoin', 'Moderate', 'Phenytoin may significantly reduce cyclosporine serum concentrations. The mechanism may be inhibition of cyclosporine absorption or induction of hepatic CYP450 metabolism or both. This interaction may occur with ethotoin, fosphenytoin, and mephenytoin as well.', NULL, 'Cyclosporine levels should be closely monitored during concomitant therapy and more frequently when phenytoin is started or stopped.', NULL, 'Cyclosporine levels should be closely monitored during concomitant therapy and more frequently when phenytoin is started or stopped. The dosage of cyclosporine should be adjusted to maintain therapeutic concentrations. Some experts suggest that this interaction could persist for a week or more following discontinuation of phenytoin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
