        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400664, 'Cyclosporine', 'Methotrexate', 'Moderate', 'Concomitant administration of cyclosporine and methotrexate may be associated with an increased risk of toxicity.', NULL, 'Close monitoring of cyclosporine and methotrexate concentrations, renal function, and liver enzymes is recommended during concurrent therapy.', NULL, 'Close monitoring of cyclosporine and methotrexate concentrations, renal function, and liver enzymes is recommended during concurrent therapy. Dosage adjustments may be necessary. Patients should be advised to report any nausea, vomiting, mouth sores, diarrhea, abdominal pain, dizziness, fatigue, or headache.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
