        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400685, 'Cyclosporine', 'Mycophenolic acid', 'Moderate', 'Coadministration of cyclosporine with mycophenolate mofetil (MMF) may result in decreased concentrations of mycophenolic acid (MPA). The mechanism of the interaction is cyclosporine-mediated inhibition of multidrug-resistance protein 2 (MRP-2) transporter in the biliary tract which prevents the excretion of mycophenolic acid glucuronide (MPAG) into the bile which would lead to enterohepatic recirculation of MPA. MPA levels were reduced by 30% to 50% in renal transplant patients treated with MMF and cyclosporine compared with those who received sirolimus or belatacept and similar doses of MMF. In addition, MPA levels increased significantly after discontinuation of cyclosporine. MPA has been shown to have no effect on the pharmacokinetics of cyclosporine in stable renal transplant patients.', NULL, 'Caution is advised when cyclosporine is used concomitantly with mycophenolate mofetil.', NULL, 'Caution is advised when cyclosporine is used concomitantly with mycophenolate mofetil. Dosage adjustments as well as clinical and laboratory monitoring of MPA trough levels whenever cyclosporine is added to or discontinued from the patient''s immunosuppressant regimen is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400686, 'Cyclosporine', 'Nabumetone', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the nephrotoxic effects of cyclosporine, especially if dehydration is present. The exact mechanism is unknown but is apparently unrelated to plasma cyclosporine levels. The interaction has been reported with diclofenac and sulindac.', NULL, 'Renal function should be closely monitored in patients receiving concomitant therapy with cyclosporine and NSAIDs.', NULL, 'Renal function should be closely monitored in patients receiving concomitant therapy with cyclosporine and NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
