        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400695, 'Cyclosporine', 'Neomycin', 'Moderate', 'The nephrotoxic effects of cyclosporine and aminoglycosides are additive. The combination generally should be avoided.', NULL, 'If these agents must be used concomitantly, renal function should be closely monitored.', NULL, 'If these agents must be used concomitantly, renal function should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
