        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400762, 'Cyclosporine', 'Propafenone', 'Minor', 'case report suggests that propafenone may increase cyclosporine serum concentrations by an unknown mechanism. Until more data are available, caution should be exercised with concomitant use of these agents and cyclosporine levels should be monitored.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
