    ) VALUES (1400767, 'Cyclosporine', 'Quinine', 'Moderate', 'Limited clinical data suggest that the concomitant use of quinine and cyclosporine may reduce blood levels of cyclosporine. The mechanism is unknown.', NULL, 'Prescribers should be aware of the potential for an interaction.', NULL, 'Prescribers should be aware of the potential for an interaction.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
