INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400781, 'Cyclosporine', 'Rifaximin', 'Moderate', 'Coadministration with cyclosporine may increase the plasma concentrations of orally administered drugs that are substrates of the P-glycoprotein (P-gp) transporter such as rifaximin. The proposed mechanism is increased absorption of rifaximin in the intestine due to inhibition of P-gp mediated drug efflux by cyclosporine. Cyclosporine is also an inhibitor of organic anion transporting polypeptide (OATP), breast cancer resistance protein (BCRP) and a weak inhibitor of CYP450 3A4. The inhibition of each of these transporters may also have a part in the increase in rifaximin exposure by cyclosporine.', NULL, 'Caution is advised if cyclosporine must be used concurrently with rifaximin.', NULL, 'Caution is advised if cyclosporine must be used concurrently with rifaximin. Monitoring for increased rifaximin adverse effects such as headache, nausea and peripheral edema is recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1400782, 'Cyclosporine', 'Rilonacept', 'Moderate', 'The use of interleukin blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin inhibitors in patients with chronic inflammatory diseases.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible. Caution is advised when interleukin inhibitors are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges such as immunosuppressants or antineoplastic agents. Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of interleukin inhibitor therapy, and the dosage(s) of these drugs adjusted accordingly. Clinicians should note that the effects of interleukin inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
