    ) VALUES (1400790, 'Cyclosporine', 'Rocuronium', 'Moderate', 'Cyclosporine may enhance and prolong the neuromuscular blockade of the nondepolarizing skeletal muscle relaxants. The mechanism is unknown.', NULL, 'Patients should be monitored for excessive and/or prolonged neuromuscular blockade.', NULL, 'Patients should be monitored for excessive and/or prolonged neuromuscular blockade.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
