INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400844, 'Cyclosporine', 'Sulfinpyrazone', 'Moderate', 'The coadministration with sulfinpyrazone may decrease the blood concentrations and pharmacologic effects of cyclosporine. The mechanism is unknown.', 'DDInter', 1767317476, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if cyclosporine must be coadministered with sulfinpyrazone. Cyclosporine blood levels should be monitored regularly and the dosage adjusted accordingly, particularly following initiation or discontinuation of sulfinpyrazone therapy in patients who are stabilized on their anti-rejection regimen.', 'Antagonism', 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if cyclosporine must be coadministered with sulfinpyrazone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
