INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400924, 'Cyclosporine', 'Ziprasidone', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400925, 'Cyclosporine', 'Zoledronic acid', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', 'DDInter', 1767317476, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400926, 'Amikacin (liposome)', 'Diatrizoate', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 1767317476, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
