INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400929, 'Diatrizoate', 'Arbutamine', 'Moderate', 'Addition of an inotropic agent to ionic radiocontrast media may produce a paradoxical depressant response that can be deleterious to the ischemic myocardium. The exact mechanism of interaction is unknown, although ionic radiocontrast agents are known to have a direct depressant effect on cardiac muscles.', 'DDInter', 1767317476, 'Clinicians should be aware of this potential interaction when an inotropic agent is used in combination with ionic radiocontrast media. Patients should be monitored closely for adverse cardiac effects.', 'Others', 'Clinicians should be aware of this potential interaction when an inotropic agent is used in combination with ionic radiocontrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400930, 'Diatrizoate', 'Bendroflumethiazide', 'Moderate', 'Forced diuresis during administration of radiocontrast agents may increase the risk of renal impairment in patients who are at high risk for contrast-induced nephropathy.', 'DDInter', 1767317476, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy. Otherwise, some experts recommend discontinuing diuretics 1 to 2 days before administration of contrast media, depending on the clinical feasibility of doing so. The smallest effective dose of a nonionic, hypo- or iso-osmolar contrast medium (e.g., iohexol, iodixanol, iopamidol) should be used, since the risk of nephropathy is increased with increasing contrast dose and/or osmolarity. Repeat procedures with contrast media, if necessary, should not occur until at least 72 hours after the previous contrast exposure and renal function has fully recovered. Although it is not necessary to measure the serum creatinine levels of every patient before contrast administration, measurements should generally be made in patients receiving contrast agent by intraarterial administration (which is associated with increased risk of nephropathy relative to intravenous administration) and patients with a history of kidney disease, proteinuria, kidney surgery, diabetes, hypertension, gout, or other risk factors for nephropathy. Creatinine measurements should be continued for 24 to 48 hours after administration of contrast medium. It is important that patients be adequately hydrated with either saline or sodium bicarbonate.', 'Synergism', 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400931, 'Diatrizoate', 'Benzthiazide', 'Moderate', 'Forced diuresis during administration of radiocontrast agents may increase the risk of renal impairment in patients who are at high risk for contrast-induced nephropathy.', 'DDInter', 1767317476, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy. Otherwise, some experts recommend discontinuing diuretics 1 to 2 days before administration of contrast media, depending on the clinical feasibility of doing so. The smallest effective dose of a nonionic, hypo- or iso-osmolar contrast medium (e.g., iohexol, iodixanol, iopamidol) should be used, since the risk of nephropathy is increased with increasing contrast dose and/or osmolarity. Repeat procedures with contrast media, if necessary, should not occur until at least 72 hours after the previous contrast exposure and renal function has fully recovered. Although it is not necessary to measure the serum creatinine levels of every patient before contrast administration, measurements should generally be made in patients receiving contrast agent by intraarterial administration (which is associated with increased risk of nephropathy relative to intravenous administration) and patients with a history of kidney disease, proteinuria, kidney surgery, diabetes, hypertension, gout, or other risk factors for nephropathy. Creatinine measurements should be continued for 24 to 48 hours after administration of contrast medium. It is important that patients be adequately hydrated with either saline or sodium bicarbonate.', 'Synergism', 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400932, 'Diatrizoate', 'Bepridil', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 1767317476, 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400933, 'Diatrizoate', 'Bromfenac', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 1767317476, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400934, 'Diatrizoate', 'Carteolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', 'DDInter', 1767317476, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400935, 'Diatrizoate', 'Deferasirox', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 1767317476, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
