INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1400989, 'Diatrizoate', 'Milrinone', 'Moderate', 'Addition of an inotropic agent to ionic radiocontrast media may produce a paradoxical depressant response that can be deleterious to the ischemic myocardium. The exact mechanism of interaction is unknown, although ionic radiocontrast agents are known to have a direct depressant effect on cardiac muscles.', 'DDInter', 1767317476, 'Clinicians should be aware of this potential interaction when an inotropic agent is used in combination with ionic radiocontrast media. Patients should be monitored closely for adverse cardiac effects.', 'Others', 'Clinicians should be aware of this potential interaction when an inotropic agent is used in combination with ionic radiocontrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
