INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401012, 'Diatrizoate', 'Procainamide', 'Moderate', 'The use of iodine-containing contrast media for coronary angiography in patients treated with certain antiarrhythmics such as amiodarone may result in significant prolongation of the QT interval. These contrast agents can be arrhythmogenic when injected into the coronary arteries and may have additive effects on cardiac repolarization when coadministered with antiarrhythmic agents that prolong the QT interval.', 'DDInter', 1767317476, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, ibutilide, sotalol) antiarrhythmic agents. Increased surveillance and ECG monitoring may be appropriate. Patients who receive outpatient angiographies should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of arrhythmia such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401013, 'Diatrizoate', 'Propranolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', 'DDInter', 1767317476, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
