INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401189, 'Dapsone (topical)', 'Dabrafenib', 'Minor', 'Theoretically, coadministration of dapsone with inducers of CYP450 3A4 such as rifamycins, anticonvulsants, barbiturates, and St. John''s wort may increase the formation of dapsone hydroxylamine, a metabolite associated with hemolysis. Dose-related hemolysis is the most common adverse event associated with oral dapsone in both patients with or without glucose-6-phosphate dehydrogenase (G6PD) deficiency.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
