INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401208, 'Drospirenone', 'Dabrafenib', 'Major', 'RECOMMENDED: Coadministration with dabrafenib may decrease the plasma concentrations and efficacy of contraceptive hormones. The mechanism is dabrafenib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317476, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib. Because use of dabrafenib may cause fetal harm, it is particularly important that patients not become pregnant during treatment. Female patients of reproductive potential should use a highly effective, non-hormonal method of contraception during treatment and for 4 weeks after treatment. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed. Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
