INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401219, 'Erdafitinib', 'Dabrafenib', 'Major', 'Coadministration with moderate inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 1767317476, 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered. If a moderate CYP450 2C9 or 3A4 inducer is coadministered after the initial dose increase period, the dose of erdafitinib may be increased up to 9 mg once daily based on serum phosphate levels and tolerability. When a moderate CYP450 2C9 or 3A4 inducer is discontinued, the current dose of erdafitinib may be continued in the absence of erdafitinib toxicity.', 'Metabolism', 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered.', NULL, NULL, 'Major', 'pharmacodynamic');
