INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401234, 'Etravirine', 'Dabrafenib', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations of etravirine, which is a substrate of CYP450 2C19, 2C9, and 3A4.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is used with CYP450 inducers. Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if etravirine is used with CYP450 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401235, 'Everolimus', 'Dabrafenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317476, 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine. If concomitant use is required, a dose adjustment of everolimus should be considered to achieve the recommended therapeutic range for the condition being treated. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase and 2 weeks after discontinuation of the inducer.', 'Metabolism', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401236, 'Exemestane', 'Dabrafenib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401237, 'Exenatide', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9, including many antidiabetic agents such as nateglinide, repaglinide, linagliptin, saxagliptin, and sulfonylureas.', 'DDInter', 1767317476, 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of dabrafenib, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination. Likewise, patients should be observed for hypoglycemia when dabrafenib is withdrawn from their therapeutic regimen.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401238, 'Fedratinib', 'Dabrafenib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of fedratinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401239, 'Felodipine', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401240, 'Fesoterodine', 'Dabrafenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers. The possibility of diminished therapeutic effects should be considered.', 'Metabolism', 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
